mapper.select:
select co.id,u1.name as owner,dv1.value as source,cu.name as customer_id,co.full_name,dv2.value as appellation,co.email,co.mphone,co.job,co.birth, u2.name as create_by,co.create_time,u3.name as edit_by,co.edit_time,co.description,co.next_contact_time,co.address from tbl_contacts co join tbl_user u1 on co.owner = u1.id left join tbl_dic_value dv1 on co.source = dv1.id left join tbl_customer cu on co.customer_id = cu.id left join tbl_dic_value dv2 on co.appellation = dv2.id join tbl_user u2 on co.create_by = u2.id left join tbl_user u3 on co.edit_by = u3.id
and u1.name like '%' #{owner} '%'
and co.full_name like '%' #{fullName} '%'
and cu.name like '%' #{customerName} '%'
and co.source = #{source}
and co.birth = #{birth}
order by co.create_time limit #{beginNo},#{pageSize}

select count(*) from tbl_contacts co join tbl_user u1 on co.owner = u1.id left join tbl_dic_value dv1 on co.source = dv1.id left join tbl_customer cu on co.customer_id = cu.id left join tbl_dic_value dv2 on co.appellation = dv2.id join tbl_user u2 on co.create_by = u2.id left join tbl_user u3 on co.edit_by = u3.id
and u1.name like '%' #{owner} '%'
and co.full_name like '%' #{fullName} '%'
and cu.name like '%' #{customerName} '%'
and co.source = #{source}
and co.birth = #{birth}
