mapper.select:
select t.id,u1.name as owner,t.money,t.name,t.expected_date,c.name as customer_id,dv1.value as stage,t.possibility,dv2.value as type,dv3.value as source,ct.full_name as contacts_id from tbl_tran t join tbl_user u1 on t.owner = u1.id join tbl_customer c on t.customer_id = c.id join tbl_dic_value dv1 on t.stage = dv1.id left join tbl_dic_value dv2 on t.type = dv2.id left join tbl_dic_value dv3 on t.source = dv3.id left join tbl_contacts ct on t.contacts_id = ct.id
and u1.name like '%' #{ownerName} '%'
and c.name like '%' #{customerName} '%'
and t.name like '%' #{tranName} '%'
and ct.full_name like '%' #{contactsName} '%'
and dv1.id = #{stage}
and dv2.id = #{type}
and dv3.id = #{source}
order by t.create_time desc limit #{beginNo},#{pageSize}

select count(*) from tbl_tran t join tbl_user u1 on t.owner = u1.id join tbl_customer c on t.customer_id = c.id join tbl_dic_value dv1 on t.stage = dv1.id left join tbl_dic_value dv2 on t.type = dv2.id left join tbl_dic_value dv3 on t.source = dv3.id left join tbl_contacts ct on t.contacts_id = ct.id
and u1.name like '%' #{ownerName} '%'
and c.name like '%' #{customerName} '%'
and t.name like '%' #{tranName} '%'
and ct.full_name like '%' #{contactsName} '%'
and dv1.id = #{stage}
and dv2.id = #{type}
and dv3.id = #{source}
